mapper.select:
SELECT * FROM file
WHERE id = #{fileId}
  AND status = true

SELECT * FROM file
WHERE file_name = #{fileName}
  AND folder_id = #{folderId}
  AND status = true
  AND is_deleted = false

mapper.insert:
INSERT INTO file (
    file_name, folder_id, file_size, file_type, file_md5,
    create_time, status, user_id, file_path
)
VALUES (
           #{fileName}, #{folderId}, #{fileSize}, #{fileType}, #{fileMd5},
           #{createTime}, #{status}, #{userId}, #{filePath}
       )

mapper.update:
UPDATE file
SET is_deleted = true,
deleted_time = NOW(),
update_time = NOW()
WHERE folder_id = #{folderId}
AND user_id = #{userId}

update.if:
UPDATE file SET is_deleted = true, deleted_time = NOW(), deleted_by = #{userId}, update_time = NOW() WHERE id IN
#{fileId}
AND user_id = #{userId} AND is_deleted = false

UPDATE file SET is_deleted = false, deleted_time = NULL, deleted_by = NULL, update_time = NOW() WHERE id IN
#{fileId}
AND user_id = #{userId} AND is_deleted = true

mapper.delete:
DELETE FROM file
WHERE id = #{fileId}
  AND user_id = #{userId}
  AND is_deleted = true
  AND deleted_by = #{userId}

mapper.select:
SELECT COUNT(*) FROM file
WHERE user_id = #{userId}
  AND is_deleted = true
