mapper.select:
SELECT * FROM folder
WHERE id = #{folderId}
  AND status = true

SELECT * FROM folder
WHERE folder_name = #{folderName}
  AND parent_id = #{parentId}
  AND status = true
  AND is_deleted = false

mapper.update:
UPDATE folder SET is_deleted = true, deleted_time = NOW(), deleted_by = #{userId}, update_time = NOW() WHERE id IN
#{folderId}
AND user_id = #{userId} AND is_deleted = false

UPDATE folder SET is_deleted = false, deleted_time = NULL, deleted_by = NULL, update_time = NOW() WHERE id IN
#{folderId}
AND user_id = #{userId} AND is_deleted = true

mapper.delete:
DELETE FROM folder
WHERE id IN

    #{folderId}

AND user_id = #{userId}
AND is_deleted = true

mapper.select:
SELECT * FROM folder
WHERE user_id = #{userId}
  AND is_deleted = true
ORDER BY deleted_time DESC
    LIMIT #{offset}, #{pageSize}

SELECT COUNT(*) FROM folder
WHERE user_id = #{userId}
  AND is_deleted = true
